import React, { Component } from 'react';
import { SplitButton, MenuItem } from 'react-bootstrap';
import ButtonToolBar from 'react-bootstrap/lib/ButtonToolbar';
import StarRatings from 'react-star-ratings';
import classes from './Filter.css';
import axios from 'axios'; 


class Filter extends Component {
    state = {
        options: {
            rating: '',
            category: '',
            price_range: '',
            food_quality: '',
            service_quality: '',
            transit_score: '',
            walk_score: '',
            bike_score: '',
            happy_hour: ''
        },
        error: false,
        categories:[]
    }

    componentDidMount(){
        let categories = [];
        axios.get('http://3.16.29.66:8080/PhillyGastronomer/category').then(response => {
            console.log(response);
            categories = response.data;
        })
        .catch( error => {
            console.log( error );
            this.setState({error: true});
        });
        console.log(categories);
        categories.push(<MenuItem eventKey="">Unselect</MenuItem>);
        if (!this.state.error) {
            categories = categories.map((category) => {
                console.log(category);
                return (<MenuItem eventKey={category.category}>{category.category}</MenuItem>);
            });

        } else {
            categories.push(<MenuItem eventKey="American">American</MenuItem>);
            categories.push(<MenuItem eventKey="Mexican">Mexican</MenuItem>);
            categories.push(<MenuItem eventKey="Asian">Asian</MenuItem>);
            categories.push(<MenuItem eventKey="Chinese">Chinese</MenuItem>);
            categories.push(<MenuItem eventKey="Japanese">Japanese</MenuItem>);
            categories.push(<MenuItem eventKey="Korean">Korean</MenuItem>);
        }
        this.setState({categories: categories});
    }

    selectRating(event) {
        var options = this.state.options;
        options.rating = event;
        this.setState({ options: options });
        this.props.onUpdate(this.state.options);
    }

    selectCategory(event) {
        var options = this.state.options;
        options.category = event;
        this.setState({ options: options });
        this.props.onUpdate(this.state.options);
    }

    selectPriceRange(event) {
        var options = this.state.options;
        options.price_range = event;
        this.setState({ options: options });
        this.props.onUpdate(this.state.options);
    }

    selectFoodQuality(event) {
        var options = this.state.options;
        options.food_quality = event;
        this.setState({ options: options });
        this.props.onUpdate(this.state.options);
    }

    selectServiceQuality(event) {
        var options = this.state.options;
        options.service_quality = event;
        this.setState({ options: options });
        this.props.onUpdate(this.state.options);
    }

    selectTransitScore(event) {
        var options = this.state.options;
        options.transit_score = event;
        this.setState({ options: options });
        this.props.onUpdate(this.state.options);
    }

    selectWalkScore(event) {
        var options = this.state.options;
        options.walk_score = event;
        this.setState({ options: options });
        this.props.onUpdate(this.state.options);
    }

    selectBikeScore(event) {
        var options = this.state.options;
        options.bike_score = event;
        this.setState({ options: options });
        this.props.onUpdate(this.state.options);
    }

    selectHappyHour(event) {
        var options = this.state.options;
        options.happy_hour = event;
        this.setState({ options: options });
        this.props.onUpdate(this.state.options);
    }

    clearValue() {
        var options = this.state.options;
        options.rating = '';
        options.category = '';
        options.price_range = '';
        options.food_quality = '';
        options.service_quality = '';
        options.transit_score = '';
        options.walk_score = '';
        options.bike_score = '';
        options.happy_hour = '';
        this.setState({options: options});
    }

    render() {
        return (
            <div>
                <ButtonToolBar className={classes.buttonToolBar}>
                    <SplitButton
                        title={'Rating: ' + this.state.options.rating}
                        onSelect={this.selectRating.bind(this)}
                        id={'splitbutton-rating'}>
                        <MenuItem eventKey="5">
                            <StarRatings
                                rating={5}
                                starRatedColor="red"
                                numberOfStars={5}
                                starDimension="20px"
                                starSpacing="5px"
                            />
                        </MenuItem>
                        <MenuItem eventKey="4">
                            <StarRatings
                                rating={4}
                                starRatedColor="red"
                                numberOfStars={5}
                                starDimension="20px"
                                starSpacing="5px"
                            />
                        </MenuItem>
                        <MenuItem eventKey="3">
                            <StarRatings
                                rating={3}
                                starRatedColor="red"
                                numberOfStars={5}
                                starDimension="20px"
                                starSpacing="5px"
                            />
                        </MenuItem>
                        <MenuItem eventKey="2">
                            <StarRatings
                                rating={2}
                                starRatedColor="red"
                                numberOfStars={5}
                                starDimension="20px"
                                starSpacing="5px"
                            />
                        </MenuItem>
                        <MenuItem eventKey="1">
                            <StarRatings
                                rating={1}
                                starRatedColor="red"
                                numberOfStars={5}
                                starDimension="20px"
                                starSpacing="5px"
                            />
                        </MenuItem>
                    </SplitButton>
                    <SplitButton
                        title={'Category: ' + this.state.options.category}
                        id={'splitbutton-category'}
                        onSelect={this.selectCategory.bind(this)}
                    > 
                    {this.state.categories}
                    </SplitButton>
                    <SplitButton
                        title={'Price range: ' + this.state.options.price_range}
                        id={'splitbutton-pricerange'}
                        onSelect={this.selectPriceRange.bind(this)}
                    >
                        <MenuItem eventKey="$$$$">$$$$</MenuItem>
                        <MenuItem eventKey="$$$">$$$</MenuItem>
                        <MenuItem eventKey="$$">$$</MenuItem>
                        <MenuItem eventKey="$">$</MenuItem>
