import React, { Component } from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import RaisedButton from 'material-ui/RaisedButton';
import Login from '../Login/Login';
import Register from '../Register/Register';
import './LoginScreen.css';

class LoginScreen extends Component {
    state = {
        username: '',
        password: '',
        loginscreen: [],
        loginmessage: '',
        buttonLabel: 'Register',
        isLogin: true
    }

    componentWillMount() {
        var loginscreen = [];
        loginscreen.push(<Login key={login} parentContext={this} appContext={this.props.parentContext} className="container"/>);
        var loginmessage = "Not registered yet? Register Now!";
        this.setState({
            loginscreen: loginscreen,
            loginmessage: loginmessage
        })
    }

    handleClick() {
        var loginmessage;
        if (this.state.isLogin) {
            var loginscreen = [];
            loginscreen.push(<Register parentContext={this} />);
            loginmessage = "Already registered? Go to Login!";
            this.setState({
                loginscreen: loginscreen,
                loginmessage: loginmessage,
                buttonLabel: "Login",
                isLogin: false
            })
        }
        else {
            var loginscreen = [];
            loginscreen.push(<Login parentContext={this} />);
            loginmessage = "Not Registered yet.Go to registration";
            this.setState({
                loginscreen: loginscreen,
                loginmessage: loginmessage,
                buttonLabel: "Register",
                isLogin: true
            })
        }
    }

    render() {
        return (
            <div className="container">
                {this.state.loginscreen}
                <div>
                    {this.state.loginmessage}
                    <MuiThemeProvider>
                        <div>
                            <RaisedButton className="button" label={this.state.buttonLabel} primary={true} style={style} onClick={(event) => this.handleClick(event)} />
                        </div>
                    </MuiThemeProvider>
                </div>
            </div>
        );
    }
}
const style = {
    margin: 15,
};
export default LoginScreen;
